//componenets/AddNode.js

// This component is designed to be used within a parent component that manages the state for title, content, and the onAddNote function. The parent component would pass these as props to the AddNote component. 
 
// This line imports the React library, which is necessary for creating React components and using JSX (JavaScript XML).
import React from "react";
 

// Here, we declare a functional component named AddNote. It takes an object as a parameter, which is destructured to extract title, setTitle, content, setContent, and onAddNote. These are props passed to the component
const AddNote = ({ title, setTitle, content, setContent, onAddNote }) => {
    // return statement contains JSX, which defines the UI structure of the component. 
    return (
        <div>
            <h2>Add Note</h2>
            <input
                type="text"
                // The value of the input is controlled by the title prop.
                placeholder="Title"
                value={title}
                // An event handler that updates the title state using the setTitle function whenever the input value changes.
                onChange={(e) => setTitle(e.target.value)}
            />
            <textarea
                placeholder="Content"
                // The value of the textarea is controlled by the content prop.
                value={content}
                // An event handler that updates the content state using the setContent function whenever the textarea value changes.
                onChange={(e) => setContent(e.target.value)}
            ></textarea>
            <button 
            // in JSX we specifically make use of className instead of class because class is a reserved keyword in javascript
            // Applies a CSS class named "button1" to style the button.
            className="button1" 
            // An event handler that calls the onAddNote function when the button is clicked.
            onClick={onAddNote}>
                Add Note
            </button>
        </div>
    );
};
 
export default AddNote;
